/**
 *
 * @type {{startFlow: function(*, *): void, stopFlow: function(*, *): void}|{startFlow?: function(*, *): void, stopFlow?: function(*, *): void}}
 */
var flows = require("./flows.js");
const express = require("express");

module.exports = function(app) {
    app.use(express.json())
    app.post('/startFlow', flows.startFlow)
    app.post('/stopFlow', flows.stopFlow)
}